import { useState } from "react";
import { useProducts, } from "../context/productsContext";
export const Filters = () => {
  const { filterDispatch, filterState,allCategories } = useProducts();
  const [categoriesList,setCategoriesList]=useState(allCategories)
  const rating = [1, 2, 3, 4];
  // let categoriesList=allCategories;
 console.log(filterState.categories);
   return (
    <div style={{ width: "15vw", minHeight: "100vh" }}>
      <form action="#">
        <span className="filterPrice">Price</span>
        <input
          type="range"
          min="1000"
          max="170000"
          step="1000"
          onChange={(e) =>{
            filterDispatch({ type: "SET_MAX_PRICE", payload: e.target.value })
            console.log(e.target.value);
          }
          }
          value={filterState.maxPrice}
        />
        <span>Category</span>
        {filterState.categories.map((name, index) => (
          <span
            style={{ display: "block", textAlign: "left", margin: "0 0.5rem" }}
            key={index}
          >
            <input
             
              type="checkbox"
              // value={value}
              // id={category}
              // name={category}
              // onChange={() =>
              //   filterDispatch({ type: "SORT_BY_CATEGORY", payload: value })
              // }
              checked={filterState.categories.includes(name)}/>
            <label htmlFor={name}>{name}</label>
          </span>
        ))}
        <span>Rating</span>
        {rating.map((rating,index) => (
          <span
            style={{ display: "block", textAlign: "left", margin: "0 0.5rem" }}
            key={index}
          >
            <input
              type="radio"
              id={rating}
              value={rating}
              name="rating"
              onChange={() =>
                filterDispatch({ type: "SORT_BY_RATING", payload: rating })
              }
            />
            <label htmlFor={rating}>{rating} Stars & above</label>
          </span>
        ))}
        <span>Sort By Price</span>
        <span
          style={{ display: "block", textAlign: "left", margin: "0 0.5rem" }}
        >
          <input
            type="radio"
            id="low"
            value="low"
            name="price"
            onChange={() =>
              filterDispatch({ type: "SORT_BY_PRICE", payload: "low" })
            }
          />
          <label htmlFor="low">Low to High</label>
        </span>
        <span
          style={{ display: "block", textAlign: "left", margin: "0 0.5rem" }}
        >
          <input
            type="radio"
            id="high"
            value="low"
            name="price"
            onChange={() =>
              filterDispatch({ type: "SORT_BY_PRICE", payload: "high" })
            }
          />
          <label htmlFor="high">High to Low</label>
        </span>
        <input
          type="reset"
          value="Clear Filters"
          onClick={() =>{ 
            setCategoriesList([...allCategories,{ category: " accessories",
            icon: "https://rukminim1.flixcart.com/flap/128/128/image/69c6589653afdb9a.png?q=100",
            value: "corAccessories",}]);
            filterDispatch({ type: "RESET_FILTERS" })}}
        />
      </form>
    </div>
  );
};
