import { Toast } from "react-toastify/dist/components";
export const notify=()=>{
    toast.success('ðŸ¦„ Wow so easy!', {
        position: "top-right",
        autoClose: 500,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "light",
        });
};
