import { useState } from "react";
import { useNavigate } from "react-router";
import { useCheckout } from "../../context/checkoutContext";
export const Address=()=>{
    const navigate=useNavigate();
    const {checkoutDetails,setCheckoutDetails}=useCheckout();
    const currentAddress={
        firstName:localStorage.getItem("firstName"),
        lastName:localStorage.getItem("lastName"),
        city:localStorage.getItem("city"),
        state:localStorage.getItem("state"),
        country:localStorage.getItem("country"),
        pinCode:localStorage.getItem("pin"),
        phone:localStorage.getItem("phone")
    }
    const newaddress={
        firstName:"",
        lastName:"",
        phone:"",
        city:"",
        state:"",
        country:"",
        pinCode:"",
    }
    const [address,setAddress]=useState({...newaddress});
    const [showAddressForm,setShowAddressForm]=useState(false);
    const [currentAddressSelected,setCurrentAddressSelected]=useState(true);
    const [proceed,setProceed]=useState(true);
    const {firstName,lastName,city,state,country,pinCode,phone}=currentAddress;
    const submitForm=(e)=>{
        e.preventDefault();
        const check=Object.values(address).reduce((acc,curr)=>curr!=="" && acc?true:false,true)
        
        check?setProceed(true):alert("Enter valid credentials")
    }
    const updateAddress=(e)=>{
        e.preventDefault();
        setProceed(false);
        setAddress({...currentAddress});
        setShowAddressForm(true);
    }
    const removeAddress=(e)=>{
        e.preventDefault();
        setProceed(false);
        setAddress({...newaddress});
        setShowAddressForm(true)
    }
    const selectAddress=(e)=>{
        if(e.target.value!=="remove" && e.target.value!=="update")
        {setAddress({...currentAddress})
        setCurrentAddressSelected(true)
        setProceed(true)}
    }
    const changeHandler=(attribute,value)=>{
        setAddress({...address,[attribute]:value})
    }
    const proceedToPayment=()=>{
        setCheckoutDetails({...checkoutDetails,address});
        goTo();
    }
    const goTo=()=>{
        navigate('/payments',{state:checkoutDetails});
    }
    return <>
    <h1>Shipment Address</h1>
    <div  style={{padding:"1rem",border:currentAddressSelected?"2px solid red":"1px solid grey",display:showAddressForm?"none":"block"}}  onClick={selectAddress}>
        <h3>{`${firstName} ${lastName}`}</h3>
        <address>
        {phone} <br />
        {`${city},${state},${country}`}<br/>
        <p>Pin Code:{pinCode}</p>
        </address>
        <button onClick={(e)=>{removeAddress(e)}} value="remove">Remove</button>
        <button onClick={updateAddress} value="update">Update</button>
    </div>
    <div>
        <div style={{padding:"1rem",border:"2px dashed black",display:showAddressForm?"none":"block"}} onClick={()=>{setShowAddressForm(true)
        setAddress({...newaddress})
        setProceed(false)
        }}>
            Add a new address
        </div>
        <form action="#" style={{display:showAddressForm?"block":"none"}}>
            <input type="text" name="FirstName" placeholder="first name" defaultValue={address.firstName} onChange={(e)=>changeHandler('firstName',e.target.value)}/>
            <input type="text" name="LastName" placeholder="last name"  defaultValue={address.lastName} onChange={(e)=>{changeHandler('lastName',e.target.value)}}/>
            <input type="text" name="phone" placeholder="phone"  defaultValue={address.phone} onChange={(e)=>{changeHandler('phone',e.target.value)}}/>
            <input type="text" name="city" placeholder="city"  defaultValue={address.city} onChange={(e)=>{changeHandler('city',e.target.value)}}/>
            <input type="text" name="state" placeholder="state"  defaultValue={address.state} onChange={(e)=>{changeHandler('state',e.target.value)}}/>
            <input type="text" name="country" placeholder="country"  defaultValue={address.country} onChange={(e)=>{changeHandler('country',e.target.value)}}/>
            <input type="text" name="PinCode" placeholder="Pin Code"  defaultValue={address.pinCode} onChange={(e)=>{changeHandler('pinCode',e.target.value)}}/>
            <input type="submit" value="Submit" onClick={submitForm} />
        </form>
    </div>
    <button disabled={!proceed} onClick={proceedToPayment}>Proceed</button>
    </>
}
