import { useState } from "react"
export const Address=()=>{
    const currentAddress={
        firstName:localStorage.getItem("firstName"),
        lastName:localStorage.getItem("lastName"),
        city:localStorage.getItem("city"),
        state:localStorage.getItem("state"),
        country:localStorage.getItem("country"),
        pinCode:localStorage.getItem("pin"),
        phone:localStorage.getItem("phone")
    }
    const [address,setAddress]=useState({
        firstName:"",
        lastName:"",
        phone:"",
        city:"",
        state:"",
        country:"",
        pinCode:"",
    });
    const [showAddressForm,setShowAddressForm]=useState(false);
    const [currentAddressSelected,setCurrentAddressSelected]=useState(false);
    const {firstName,lastName,city,state,country,pinCode,phone}=currentAddress;
    const submitForm=(e)=>{
        e.preventDefault();
    }
    const updateAddress=(e)=>{
        e.preventDefault();
        setAddress({...currentAddress});
        setShowAddressForm(true);
    }
    console.log(showAddressForm);
    return <>
    <h1>Shipment Address</h1>
    <div  style={{padding:"1rem",border:currentAddressSelected?"2px solid red":"1px solid grey",display:showAddressForm?"none":"block"}} onClick={()=>{setAddress({...currentAddress})
    ,setCurrentAddressSelected(!currentAddressSelected)}}>
        <h3>{`${firstName} ${lastName}`}</h3>
        <address>
        {phone} <br />
        {`${city},${state},${country}`}<br/>
        <p>Pin Code:{pinCode}</p>
        </address>
        <button>Remove</button>
        <button onClick={updateAddress}>Update</button>
    </div>
    <div>
        <div style={{padding:"1rem",border:"2px dashed black",display:showAddressForm?"none":"block"}} onClick={()=>setShowAddressForm(true)}>
            Add a new address
        </div>
        <form action="#" style={{display:showAddressForm?"block":"none"}}>
            <input type="text" name="FirstName" placeholder="first name" value={address.firstName} onChange={()=>{}}/>
            <input type="text" name="LastName" placeholder="last name"  value={address.lastName} onChange={()=>{}}/>
            <input type="text" name="phone" placeholder="phone"  value={address.phone} onChange={()=>{}}/>
            <input type="text" name="city" placeholder="city"  value={address.city} onChange={()=>{}}/>
            <input type="text" name="state" placeholder="state"  value={address.state} onChange={()=>{}}/>
            <input type="text" name="country" placeholder="country"  value={address.country} onChange={()=>{}}/>
            <input type="text" name="PinCode" placeholder="Pin Code"  value={address.pinCode} onChange={()=>{}}/>
            <input type="submit" value="Submit" onClick={submitForm} />
        </form>
    </div>
    </>
}
