import { useReducer } from "react";
import { initialFilter,filterReducerFunction } from "../reducers/filterReducer";
export const Filters = () => {
    const [filterState,filterDispatch]=useReducer(filterReducerFunction,initialFilter);
  const categories = [
    {
      category: "Computer accessories",
      icon: "https://bit.ly/42ZW1Y9",
      value:"computerAccessories"
    },
    {
      category: "Mobiles",
      icon: "https://bit.ly/42ZW1Y9",
      value:"mobiles"
    },
    {
      category: "Computers",
      icon: "https://bit.ly/42ZW1Y9",
      value:"computers"
    },
    {
      category: "Networking Equipments",
      icon: "https://bit.ly/42ZW1Y9",
      value:"networking"
    },
    {
      category: "Software",
      icon: "https://bit.ly/42ZW1Y9",
      value:"software"
    },
  ];
  console.log(filterState);
  const rating=[1,2,3,4,5];
  return (
    <div style={{ width: "30vw", minHeight: "100vh" }}>
      <span className="filterPrice">Price</span>
      <input type="range" min="50" max="200" step="50" />
      <span>Category</span>
      {categories.map(({ category }, index) => (
        <span
          style={{ display: "block", textAlign: "left", margin: "0 0.5rem" }}
        >
          <input
            key={index}
            type="checkbox"
            value={category}
            id={category}
            name={category}
            onChange={()=>filterDispatch({type:"SORT_BY_CATEGORY",})}
          />
          <label htmlFor={category}>{category}</label>
        </span>
      ))}
      <span>Rating</span>
      {rating.map((rating)=>
      <span
      style={{ display: "block", textAlign: "left", margin: "0 0.5rem" }}>
      <input type="radio" id={rating} value={rating} name="rating"/>
      <label htmlFor={rating}>{rating} Stars & above</label>
      </span>
      )}
      <span>Sort By Price</span>
      <span
      style={{ display: "block", textAlign: "left", margin: "0 0.5rem" }}>
      <input type="radio" id="low" value="low" name="price" onChange={()=>filterDispatch({type:"SORT_BY_PRICE",payload:"low"})}/>
      <label htmlFor="low">Low to High</label>
      </span>
      <span
      style={{ display: "block", textAlign: "left", margin: "0 0.5rem" }}>
      <input type="radio" id="high" value="low" name="price" onChange={()=>filterDispatch({type:"SORT_BY_PRICE",payload:"high"})}/>
      <label htmlFor="high">High to Low</label>
      </span>
    </div>
  );
};
