import { userDetails } from "../services/getUser";
const initialUser = {
  firstName: localStorage.getItem("firstName"),
  lastName: localStorage.getItem("lastName"),
  gender: localStorage.getItem("gender"),
  email: localStorage.getItem("email"),
  phone: localStorage.getItem("phone"),
  address: {
    city: localStorage.getItem("city"),
    state: localStorage.getItem("state"),
    country: localStorage.getItem("country"),
    pinCode: localStorage.getItem("pin"),
  },
  loginStatus:localStorage.getItem("token") ? true : false
};
const authReducerFun = (state, action) => {
  const { type, payload } = action;
  switch (type) {
    case "LOAD_DATA":
      return initialUser;
    case "LOG_USER":
      (async () => {
        try {
            console.log(payload);
          const response = await userDetails({...payload});
          if (response?.encodedToken === undefined) throw response;
          else {
            const {
              encodedToken,
              foundUser: {
                firstName,
                lastName,
                email,
                gender,
                phone,
                address: { city, state, country, pin },
              },
            } = response;
            localStorage.setItem("token", encodedToken);
            localStorage.setItem("firstName", firstName);
            localStorage.setItem("lastName", lastName);
            localStorage.setItem("email", email);
            localStorage.setItem("gender", gender);
            localStorage.setItem("city", city);
            localStorage.setItem("state", state);
            localStorage.setItem("country", country);
            localStorage.setItem("pin", pin);
            localStorage.setItem("phone", phone);

            return {
                firstName: localStorage.getItem("firstName"),
                lastName: localStorage.getItem("lastName"),
                gender: localStorage.getItem("gender"),
                email: localStorage.getItem("email"),
                phone: localStorage.getItem("phone"),
                address: {
                  city: localStorage.getItem("city"),
                  state: localStorage.getItem("state"),
                  country: localStorage.getItem("country"),
                  pinCode: localStorage.getItem("pin"),
                },
                loginStatus:localStorage.getItem("token") ? true : false
              };
          }
        } catch (e) {
          console.log(e);
        }
      })();
  }
};
export {initialUser,authReducerFun}
