import { useEffect, useState } from "react";
import { useCart } from "../../context/cartContext"

export const OrderSummary=()=>{
    const {cartState}=useCart();
    const [itemsList,setItemsList]=useState([]);
    const initialPricing={
        items: 0,
        price: 0,
        discount: 0,
        totalPrice: 0,
    }
    const [priceDetails, setPrice] = useState(initialPricing);
    useEffect(()=>{
        const initialPricing={
            items: 0,
            price: 0,
            discount: 0,
            totalPrice: 0,
          }
          const update = () => {
            const newObj = cartState.reduce(
              (acc, { qty, price, offerPrice }) => ({
                items: cartState.length,
                price: (acc.price += parseInt(price.replaceAll(",", ""))*qty),
                discount: acc.discount+=(parseInt(price.replaceAll(",", "")) - parseInt(offerPrice.replaceAll(",", "")))*qty,
                totalPrice:(acc.price-acc.discount)
              }),
              initialPricing
            );
            setPrice(cartState.length===0?initialPricing:newObj);
          };
          update() 
        setItemsList(cartState.map(({title,qty,offerPrice,image})=>({title,qty,offerPrice,image})))
    },[cartState])
    console.log(itemsList)
    console.log(priceDetails)
    // console.log(cartState);

    return <><div>
        <h1>OrderSummary</h1>
        <div>
            <div>
                Order Details
            </div>
            <div>
                <div>Item</div>
                <div>Price</div>
                {itemsList.map((item,index)=><>
                <div key={index}>
                    <div>{item?.title} X {item?.qty}</div>
                    <div>Rs.{item?.offerPrice}</div>
                </div>
                </>)}
            </div>
            <div>Price Details</div>
            <div>
                <p>Sub total: {priceDetails?.price}</p>
                <p>Discount: {priceDetails?.discount}</p>
                <p>Grand Total: {priceDetails?.totalPrice}</p>
            </div>
        </div>
        </div></>
}
