import { useState } from "react";
import { useAuth } from "../context/authContext";
import { useCart } from "../context/cartContext";
import { useWishlist } from "../context/wishistContext";
import "../pages/User/viewUser.css";
import MaleUser from "../../images/maleUser.png";
import FemaleUser from "../../images/femaleUser.png";
import { useUser } from "../context/userContext";
export const ShowUser = () => {
  const { authDispatch, setIsLogin, authState } = useAuth();
  const { cartDispatch } = useCart();
  const { wishlistDispatch } = useWishlist();
  const { addresses } = useUser();
  const { firstName, lastName, gender, email } = authState;
  const [showTab, setShowTab] = useState(0);
  const initialData = {
    firstName: "",
    lastName: "",
    gender: "",
    city: "",
    state: "",
    country: "",
    pinCode: "",
    phone: "",
  };
  const [newAddress, setNewAddress] = useState(initialData);
  const changeHandler = (attribute, value) => {
    setNewAddress({ ...newAddress, [attribute]: value });
  };
  const submitForm=(e)=>{
    e.preventDefault();
    
  }
  console.log(addresses);
  return (
    <div className="userMenu">
      {/* <h2
        style={{ margin: "0", padding: "1rem" }}
      >{`${firstName} ${lastName}`}</h2>
      <p>Email:{email}</p>
      <p>Gender:{gender}</p>
      <p>Phone Number: {phone}</p>
      <p>Address: {`${city},${state},${country}`}</p>
      <p>Pin Code: {pin}</p>
      <button
        onClick={() => {
          authDispatch({ type: "LOG_OUT" });
          cartDispatch({ type: "RESET" });
          wishlistDispatch({ type: "RESET" });
          setIsLogin(false);
        }}
      >
        LogOut
      </button> */}
      <div className="userTabs">
        <div
          className="tabs"
          onClick={() => setShowTab(0)}
          style={{ borderBottom: showTab === 0 && "2px solid red" }}
        >
          User Info
        </div>
        <div
          className="tabs"
          onClick={() => setShowTab(1)}
          style={{ borderBottom: showTab === 1 && "2px solid red" }}
        >
          Orders
        </div>
        <div
          className="tabs"
          onClick={() => setShowTab(2)}
          style={{ borderBottom: showTab === 2 && "2px solid red" }}
        >
          Addresses
        </div>
      </div>
      <div
        style={{ display: showTab === 0 ? "block" : "none" }}
        className="tabMenu"
      >
        <img
          src={gender.toLowerCase() === "female" ? FemaleUser : MaleUser}
          alt="userAvatar"
          className="avatar"
        />
        <h2>{`${firstName} ${lastName}`}</h2>
        <p>Email: {email}</p>
        <p>Gender: {gender}</p>
        <button
          onClick={() => {
            authDispatch({ type: "LOG_OUT" });
            cartDispatch({ type: "RESET" });
            wishlistDispatch({ type: "RESET" });
            setIsLogin(false);
          }}
        >
          LogOut
        </button>
      </div>
      <div
        style={{ display: showTab === 1 ? "block" : "none" }}
        className="tabMenu"
      >
        OrdersHere
      </div>
      <div
        style={{ display: showTab === 2 ? "block" : "none" }}
        className="tabMenu"
      >
        {addresses.map((address, index) => {
          const { city, state, country, pinCode, phone, firstName, lastName } =
            address;
          return (
            <div key={index} className="addressCards">
              <p>
                {firstName} {lastName}
              </p>
              <p>Phone: {phone}</p>
              <p>
                {city},{state},{country},{pinCode}
              </p>
            </div>
          );
        })}
        <button>Add new Address</button>
        <div>
          <form action="#">
            <label htmlFor="fName">First Name</label>
            <input
              type="text"
              placeholder="FIRST NAME"
              id="fName"
              onChange={(e) => changeHandler("firstName", e.target.value)}
              required
            />
            <label htmlFor="lName">Last Name</label>
            <input
              type="text"
              placeholder="LAST NAME"
              id="lName"
              onChange={(e) => changeHandler("lastName", e.target.value)}
            />
            <label htmlFor="email">Email address</label>
            <input
              type="email"
              id="email"
              onChange={(e) => changeHandler("email", e.target.value)}
            />
            <input
              type="radio"
              id="male"
              name="gender"
              value="male"
              onChange={(e) => changeHandler("gender", e.target.value)}
            />
            <label htmlFor="male">Male</label>
            <input
              type="radio"
              id="female"
              name="gender"
              value="female"
              onChange={(e) => changeHandler("gender", e.target.value)}
            />
            <label htmlFor="female">Female</label>
            <input
              type="radio"
              id="other"
              name="gender"
              value="other"
              onChange={(e) => changeHandler("gender", e.target.value)}
            />
            <label htmlFor="other">Other</label>
            <p>Billing Address</p>
            <label htmlFor="city">City</label>
            <input
              type="text"
              id="city"
              onChange={(e) => changeHandler("city", e.target.value, true)}
            />
            <label htmlFor="state">State</label>
            <input
              type="text"
              id="state"
              onChange={(e) => changeHandler("state", e.target.value, true)}
            />
            <label htmlFor="country">Country</label>
            <input
              type="text"
              id="country"
              onChange={(e) => changeHandler("country", e.target.value, true)}
            />
            <label htmlFor="pin">Pin Code</label>
            <input
              type="text"
              id="pin"
              onChange={(e) => changeHandler("pin", e.target.value, true)}
            />
            <label htmlFor="phone">Phone</label>
            <input
              type="text"
              id="phone"
              onChange={(e) => changeHandler("phone", e.target.value)}
            />
            <input
              type="submit"
              value="Sign Up"
              onClick={(e) => submitForm(e)}
            />
          </form>
        </div>
      </div>
    </div>
  );
};
