import { useEffect, useState } from "react";

export const Slider=()=>{
    const slides=[
        {
            image:"https://shorturl.at/JYZ59",
        },
        {
            image:"https://shorturl.at/bghlP",
        },
        {
            image:"https://shorturl.at/cquT6",
        },
        {
            image:"https://shorturl.at/ceLU9"
        }
    ]
    const totalSlides=slides.length;
    const [currentSlide,setCurrentSilder]=useState(0);
    const slide=(slideNo)=>{
        return {transform:`translate(-${slideNo*100}vw)`}
    }
    const nextSlide=()=>{
        setCurrentSilder(currentSlide===totalSlides-1?0:currentSlide+1);
    }
    const prevSlide=()=>{
        setCurrentSilder(currentSlide===0?totalSlides-1:currentSlide-1);
    }
    // useEffect(()=>{
    //     const interval=setInterval(()=>setCurrentSilder(currentSlide+1),3000);

    // },[]);
    console.log(currentSlide)
    return <div className="Slider">
        <ul style={slide(currentSlide)}>
            {slides.map(({image},index)=><li className="slides" key={index}></li>)}
        </ul>
        <button onClick={prevSlide} className="sliderBtn prev"> {"<<"} </button>
        <button onClick={nextSlide} className="sliderBtn next">{">>"}</button>
    </div>
}
