const initialFilter={
    sortBy:"",
    categories:{
        computerAccessories:false,
        mobiles:false,
        computers:false,
        networking:false,
        software:false
    },
    rating:"1",
    maxPrice:"50000",
    search:""
}
const filterReducerFunction=(state,action)=>{
    const {type,payload}=action;
    switch (type)
    {   
        case "SORT_BY_PRICE": return {...state,sortBy:payload};
        case "SORT_BY_CATEGORY":
        console.log(payload)    ;
        return {...state,categories:{
            ...state.categories,
            [payload]:![payload]
        }};

        default: return state;
    }
}
export {initialFilter,filterReducerFunction};
