import { useState,useEffect,createContext,useContext } from "react";
import { userDetails } from "../services/getUser";
export const AuthContext=createContext();
export const AuthProvider=({children})=>{
const [isLogin,setIsLogin]=useState(false);
useEffect(()=>{
(async()=>{
    try{const response=await userDetails();
        if(response?.encodedToken===undefined)
        throw response;
        else{
      localStorage.setItem("token",response?.encodedToken);
      localStorage.setItem("user",response?.foundUser?.firstName);
      localStorage.setItem("email",response?.foundUser?.email);
        }
    if(localStorage.getItem("token"))
    setIsLogin(true);
    }
    catch(e){
console.log(e)
    }
})()
})
    console.log(isLogin)
    return<AuthContext.Provider value={{isLogin,setIsLogin}}>
        {children}
    </AuthContext.Provider>
}
export const useAuth=()=>useContext(AuthContext);
