import { WishListCard } from "../../components/wishlistCard"
import { useWishlist } from "../../context/wishistContext";
export const Wishlist=()=>{
    const {wishlistState}=useWishlist();
    const encodedToken=localStorage.getItem("token");
    const wishlistEmpty=wishlistState.length===0;
    return <div div style={{height:"72vh"}}>
    {
        encodedToken?
       ( wishlistEmpty?<></>:wishlistState.map((product,index)=><WishListCard product={product} key={index}/>)):
       <h1>Login First</h1> 
    }
    
    </div>
}
