import "./component CSS/cartProductCard.css";
import { increaseQty } from "../services/increaseQuantity";
import { decreaseQty } from "../services/decreaseQuantity";
import { removeFromCart } from "../services/removefromCart";
import { addProductToWishlist } from "../services/addProductToWishlist";
import { useCart } from "../context/cartContext";
export const CartProductCard=({product})=>{
    const {increaseProductQty,decreaseProductQty}=useCart();
    const token=localStorage.getItem("token");
    const {_id,title,offerPrice,price,discount,qty,image}=product;
    return <div className="cartCard">
        <div className="displayImage">
            <img src={image} alt={title} className="cartDisplayImage"/>
        </div>
        <div className="cartProductDetails">
            <span className="cartProductTitle">{title}</span>
            <p><span>₹{offerPrice}</span><span>₹{price}</span></p>
            <p>{discount}</p>
            <p>Quantity:<button onClick={()=>qty!==0?decreaseQty(_id,token):removeFromCart(_id,token)}>-</button><span>{qty}</span><button onClick={()=>increaseProductQty(_id)}>+</button></p>
            <button onClick={()=>removeFromCart(_id,token)}>Remove from Cart</button>
            <button onClick={()=>addProductToWishlist(product,token)}>Move to Wishlist</button>
        </div>
    </div>;
}
