import { filterCategory } from "./filterByCategory";

export const searchedProducts = (allProducts, filters) => {
  const { search: searchString, categories } = filters;
  //   if (searchString !== "")
  //     return filterCategory(
  //       allProducts.filter((product) => {
  //         const { title, category } = product;
  //         return (
  //           title.toLowerCase().includes(searchString) ||
  //           category.toLowerCase().includes(searchString)
  //         );
  //       })
  //     );
  //   return allProducts;
  const foundProducts =
    searchString !== ""
      ? allProducts.filter((product) => {
          const { title, category } = product;
          return (
            title.toLowerCase().includes(searchString) ||
            category.toLowerCase().includes(searchString)
          );
        })
      : allProducts;
  return filterCategory(foundProducts, categories);
};
