export const ProductDetailCard = ({ product }) => {
  const {
    category,
    features,
    discount,
    id,
    image,
    offerPrice,
    price,
    rating,
    seller,
    title,
  } = product;
  const featuresList=Object.entries(features);
  console.log(featuresList)
  console.log(product);
  return(
  <>
    <div style={{ display: "flex", flexWrap: "wrap" }}>
      <img src={image} alt={title} className="productDetailImage" />
      <div>
        <h2>{title}</h2>
        <p>{category}</p>
        <p>
          <span>₹{offerPrice}</span> <span>₹{price}</span>{" "}
          <span>{discount}</span>
        </p>
        <p>Raings: {rating}</p>
        <p>Seller: {seller}</p>
      </div>
    </div>
    <div>
        <h2>Product Details</h2>
        {featuresList.map((feature,index)=><p id={index}>
            {feature[0]}:{feature[1]}
        </p>)}
    </div>
  </>);
};
