import { useEffect, useState } from "react";
import "../pages/User/viewUser.css";
import { useUser } from "../context/userContext";
export const AddressForm = ({
  receivedAddress,
  setFormFun,
  showForm,
  setFormAddress,
  updateIndex,
  setUpdate
}) => {
  const { addNewAddress, updateAddress } = useUser();

//   const [formAvailable, setFormAvailabe] = useState(true);
  const initialData = {
    firstName: "",
    lastName: "",
    gender: "",
    city: "",
    email:"",
    state: "",
    country: "",
    pinCode: "",
    phone: "",
  };
  const [newAddress, setNewAddress] = useState(initialData);
  const changeHandler = (attribute, value) => {
    setNewAddress({ ...newAddress, [attribute]: value.trim() });
  };
  useEffect(() => {
    const initialData = {
        firstName: "",
        lastName: "",
        gender: "",
        city: "",
        email:"",
        state: "",
        country: "",
        pinCode: "",
        phone: "",
      };
    setNewAddress(receivedAddress ?? initialData);
  }, [receivedAddress]);
  const submitForm = (e) => {
    e.preventDefault();
    if (receivedAddress !== undefined) {
      updateAddress(updateIndex, newAddress);
        setUpdate(-1);
    } else addNewAddress(newAddress);
    // setFormAvailabe(false);
    setNewAddress(initialData);
    setFormFun(false);
    setFormAddress(undefined);
  };
  const isEmptyForm=Object.values(newAddress).reduce((acc,curr)=>curr===""?true:acc,false);
  return (
    <>
      <div
        className="formBackground"
        style={{ display: showForm ? "block" : "none" }}
      ></div>
      <div
        style={{
          width: "50%",
          background: "white",
          display: showForm ? "block" : "none",
        }}
        className="addressForm"
      >
        <form action="#">
          <label htmlFor="fName">First Name</label>
          <input
            type="text"
            placeholder="FIRST NAME"
            id="fName"
            name="firstName"
            onChange={(e) => changeHandler("firstName", e.target.value)}
            value={newAddress.firstName}
          />
          <label htmlFor="lName">Last Name</label>
          <input
            type="text"
            placeholder="LAST NAME"
            id="lName"
            onChange={(e) => changeHandler("lastName", e.target.value)}
            value={newAddress.lastName}
          />
          <label htmlFor="email">Email address</label>
          <input
            type="email"
            id="email"
            onChange={(e) => changeHandler("email", e.target.value)}
            value={newAddress.email}
          />
          <input
            type="radio"
            id="male"
            name="gender"
            value="male"
            onChange={(e) => changeHandler("gender", e.target.value)}
            checked={newAddress.gender.toLowerCase() === "male"}
          />
          <label htmlFor="male">Male</label>
          <input
            type="radio"
            id="female"
            name="gender"
            value="female"
            onChange={(e) => changeHandler("gender", e.target.value)}
            checked={newAddress.gender.toLowerCase() === "female"}
          />
          <label htmlFor="female">Female</label>
          <input
            type="radio"
            id="other"
            name="gender"
            value="other"
            onChange={(e) => changeHandler("gender", e.target.value)}
            checked={newAddress.gender.toLowerCase() === "other"}
          />
          <label htmlFor="other">Other</label>
          <p>Billing Address</p>
          <label htmlFor="city">City</label>
          <input
            type="text"
            id="city"
            onChange={(e) => changeHandler("city", e.target.value, true)}
            value={newAddress.city}
          />
          <label htmlFor="state">State</label>
          <input
            type="text"
            id="state"
            onChange={(e) => changeHandler("state", e.target.value, true)}
            value={newAddress.state}
          />
          <label htmlFor="country">Country</label>
          <input
            type="text"
            id="country"
            onChange={(e) => changeHandler("country", e.target.value, true)}
            value={newAddress.country}
          />
          <label htmlFor="pin">Pin Code</label>
          <input
            type="text"
            id="pin"
            onChange={(e) => changeHandler("pinCode", e.target.value, true)}
            value={newAddress.pinCode}
          />
          <label htmlFor="phone">Phone</label>
          <input
            type="text"
            id="phone"
            onChange={(e) => changeHandler("phone", e.target.value)}
            value={newAddress?.phone}
          />
          <button onClick={(e) => submitForm(e)} disabled={isEmptyForm}>{isEmptyForm?"Add Address":"Update"}</button>
          {/* <button>Close</button> */}
          <button
            onClick={(e) => {
              e.preventDefault();
            //   setFormAvailabe(false);
              setFormFun(false);
              setNewAddress(initialData);
              setFormAddress(undefined);
            }}
          >
            Close Form
          </button>
        </form>
      </div>
    </>
  );
};
