import { useState } from "react"

export const Slider=()=>{
    const [currentSlide,setCurrentSilder]=useState(0);
    const slide=(slideNo)=>{
        return {transform:`translate(-${slideNo*100}vw)`}
    }
    const nextSlide=()=>{
        setCurrentSilder((current)=>current==2?0:current+1);
    }
    setInterval(nextSlide,1000);
    console.log()
    return <div style={{height:"50vh",width:"100vw",backgroundColor:"grey",margin:"0"}} className="Slider">
        <ul style={slide(currentSlide)}>
            <li className="slides"></li>
            <li className="slides"></li>
            <li className="slides"></li>
        </ul>
        <button onClick={nextSlide}>Next</button>
    </div>
}
