export const searchedProducts=(allProducts,filters)=>{
    const {search:searchString}=filters;
    if(searchString!=="")
    return allProducts.filter(product=>{
        const {title,category}=product;
        return title.toLowerCase().includes(searchString) ||  category.toLowerCase().includes(searchString);
    })
    return allProducts;
}
