import { useState, useContext, createContext } from "react";
const UserContext = createContext();
export const UserProvider = ({ children }) => {
  const [orders,setOrders]=useState([]);
  const currentAddress={
  firstName:localStorage.getItem("firstName"),
  lastName:localStorage.getItem("lastName"),
  gender:localStorage.getItem("gender"),
  email:localStorage.getItem("email"),
  city:localStorage.getItem("city"),
  state:localStorage.getItem("state"),
  country:localStorage.getItem("country"),
  pinCode:localStorage.getItem("pin"),
  phone:localStorage.getItem("phone"),}
  const [addresses,setAddresses]=useState( [currentAddress])
  const addNewAddress=(newAddress)=>{
    setAddresses([...addresses,newAddress]);
  }
  const updateAddress=(updateIndex,updateAddress)=>{
    setAddresses(addresses=>addresses.map((address,index)=>index===updateIndex?({...address,...updateAddress}):address))
  }
  return <UserContext.Provider value={{orders,addresses,setAddresses,setOrders,addNewAddress,updateAddress}}>{children}</UserContext.Provider>;
};
export const useUser = () => useContext(UserContext);
