import axios from "axios"
export const removeFromCart=async(productId,encodedToken)=>{
try{
const response=await fetch(`/api/user/cart/${productId}`,
{  
    method:'DELETE',
    headers:{authorization:encodedToken}
}
);
console.log(response);
}
catch(e){
    console.log(e)
}
}
