import { Link, useNavigate, useLocation } from "react-router-dom";
import { useState } from "react";
import { useAuth } from "../context/authContext";
export const LoginForm = () => {
  const { user, isLogin, logOut } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [userCredential, setCredentials] = useState({
    email: "",
    password: "",
    remeberMe: true,
  });
  const changeHandler = (value, type) => {
    setCredentials({ ...userCredential, [type]: value });
  };
  const sendCredentials=(email,password)=>{
    console.log(email,password);
    return (email.trim()!=="" && password.trim()!=="")?user(`${email}`,`${password}`):console.log("Enter Credentials")};
  const { email, password } = userCredential;
  console.log(location);
  return (
    <div>
      <h2>Login</h2>
      <label htmlFor="email">Email address</label>
      <input
        type="email"
        name="email"
        id="email"
        onChange={(e) => changeHandler(e.target.value, "email")}
      />
      <label htmlFor="password">Password</label>
      <input
        type="password"
        name="password"
        id="password"
        onChange={(e) => changeHandler(e.target.value, "password")}
      />
      <input
        type="checkbox"
        name="remember"
        id="remember"
        onChange={(e) => changeHandler(e.target.checked, "remeberMe")}
        checked={userCredential.remeberMe}
      />
      <label htmlFor="remember">Remember me</label>
      {isLogin ? (
        <button onClick={() => logOut()}>Logout</button>
      ) : (
        <button
          onClick={()=>sendCredentials(email,password)}
        >
          Login
        </button>
      )}
      <button
        // onClick={() => {
        //   user({ email: "adarshbalika@gmail.com", password: "adarshbalika" });
        // }}
        onClick={()=> sendCredentials("adarshbalika@gmail.com","adarshbalika")}
      >
        Test
      </button>
      <Link>Create New Account</Link>
    </div>
  );
};
