import { CartProductCard } from "../../components/cartProduct";
import { PriceCard } from "../../components/priceCard";
import { useCart } from "../../context/cartContext";
import EmptyCart from "../../../images/cart.webp";
export const Cart=()=>{
    const {cartState}=useCart();
    const encodedToken=localStorage.getItem("token");
    const cartEmpty=cartState.length===0;
    return <div style={{height:"72vh"}}>
    {
        encodedToken ? 
        (cartEmpty?<>
        <img src={EmptyCart} alt="emptyCart" />
        Looks Like you need to Shop!</>:<>
        {cartState.map((product,index)=><CartProductCard product={product} key={index}/>)}
        </>):
        <h1>Login First</h1>    
    }
    <PriceCard/>
    </div>
}
