import { useReducer } from "react";
import { ProductCard } from "../../components/productCard";
import { useProducts } from "../../context/productsContext";
import { Filters } from "../../components/filters";
import { initialFilter,filterReducerFunction } from ".../reducers/filterReducer";
import "./products.css"
export const Products=()=>{
    const [filterState,filterDispatch]=useReducer(filterReducerFunction,initialFilter);
    const {products:{showProducts}}=useProducts();
    console.log(filterState)
    return <div style={{display:"flex"}}>
    <Filters filterDispatch={filterDispatch}/>
    {showProducts?.length!==0?
    <ul className="productsHolder">
        {showProducts?.map((product,index)=>
            <ProductCard {...product} key={index}/>
)}
    </ul>
    :<h1>Nothing to show here...</h1>}
    </div>
}
