import { useEffect, useState } from "react"

export const Slider=()=>{
    const [currentSlide,setCurrentSilder]=useState(0);
    const slide=(slideNo)=>{
        return {transform:`translate(-${slideNo*100}vw)`}
    }
    const nextSlide=()=>{
        setCurrentSilder(currentSlide==2?0:currentSlide+1);
    }
    // useEffect(()=>{
    //     const interval=setInterval(()=>setCurrentSilder(currentSlide+1),3000);

    // },[]);
    console.log(currentSlide)
    return <div style={{height:"50vh",width:"100vw",backgroundColor:"grey",margin:"0"}} className="Slider">
        <ul style={slide}>
            <li className="slides"></li>
            <li className="slides"></li>
            <li className="slides"></li>
        </ul>
        <button onClick={nextSlide}>Next</button>
    </div>
}
