import { useState, createContext, useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { userDetails } from "../services/getUser";
export const AuthContext = createContext();
export const AuthProvider = ({ children }) => {
  const [isLogin, setIsLogin] = useState(
    localStorage.getItem("token") ? true : false
  );
  const location = useLocation();
  const navigate = useNavigate();
  const user = async (email, password) => {
    try {
      const response = await userDetails({ email, password });
      if (response?.encodedToken === undefined) throw response;
      else {
        const {
          encodedToken,
          foundUser: { firstName, lastName, email, gender, address:{city,state,country,pin} },
        } = response;
        // localStorage.setItem("token", response?.encodedToken);
        // localStorage.setItem("user", response?.foundUser?.firstName);
        // localStorage.setItem("email", response?.foundUser?.email);
        localStorage.setItem("token", encodedToken);
        localStorage.setItem("user", currentUser);
        localStorage.setItem("firstName",firstName);
        localStorage.setItem("lastName",lastName);
        localStorage.setItem("email",email);
        localStorage.setItem("gender",gender);
        localStorage.setItem("city",city);
        localStorage.setItem("state",state);
        localStorage.setItem("country",country);
        localStorage.setItem("pin",pin);
      }
      if (localStorage.getItem("token")) {
        setIsLogin(true);
        navigate(location?.state?.pathname);
      }
    } catch (e) {
      console.log(e);
    }
  };
  console.log(currentUser);
  const logOut = () => {
    // localStorage.removeItem("token");
    // localStorage.removeItem("user");
    // localStorage.removeItem("email");
    localStorage.clear()
    setIsLogin(false);
  };
  // const response=userDetails({email: "adarshbalika@gmail.com", password: "adarshbalika",});
  //     console.log(response)
  return (
    <AuthContext.Provider
      value={{ isLogin, setIsLogin, user, logOut }}
    >
      {children}
    </AuthContext.Provider>
  );
};
export const useAuth = () => useContext(AuthContext);
