import { Link,useNavigate,useLocation } from "react-router-dom";
import { useState } from "react";
import { useAuth } from "../context/authContext";
export const LoginForm = () => {
  const { user, isLogin,logOut } = useAuth();
  const location=useLocation();
  const navigate=useNavigate();
  const [userCredential,setCredentials]=useState({
    email:"",
    password:"",
    remeberMe:true
  });
  const changeHandler=(value,type)=>{
    // console.log(e.target.value,userCredential[type]);
   setCredentials({...userCredential,[type]:value})
  }
  console.log(userCredential);
  console.log(location);
  return (
    <div>
      <h2>Login</h2>
      <label htmlFor="email">Email address</label>
      <input type="email" name="email" id="email" onChange={(e)=>changeHandler(e.target.value,"email")}/>
      <label htmlFor="password">Password</label>
      <input type="password" name="password" id="password" />
      <input type="checkbox" name="remember" id="remember" onChange={(e)=>changeHandler(e.target.checked,"remeberMe")} checked={userCredential.remeberMe}/>
      <label htmlFor="remember">Remember me</label>
      {isLogin?
      <button
      onClick={()=>logOut()}
      >Logout
      </button>
      :
      <button
        onClick={() =>{
          user({ email: "adarshbalika@gmail.com", password: "adarshbalika" })
          
        }
        }
      >
        Login
      </button>}
      <Link>Create New Account</Link>
    </div>
  );
};
