import {createContext,useContext,useState,useEffect} from "react";
import { useCart } from "./cartContext";
const CheckoutContext=createContext();
export const CheckoutProvider=({children})=>{
    const {cartState}=useCart();

const [checkoutDetails,setCheckoutDetails]=useState({});
useEffect(()=>{
    const initialPricing={
        items: 0,
        price: 0,
        discount: 0,
        totalPrice: 0,
      }
    const prices= cartState.reduce(
        (acc, { qty, price, offerPrice }) => ({
          items: cartState.length,
          price: (acc.price += parseInt(price.replaceAll(",", ""))*qty),
          discount: acc.discount+=(parseInt(price.replaceAll(",", "")) - parseInt(offerPrice.replaceAll(",", "")))*qty,
          totalPrice:(acc.price-acc.discount)
        }),
        initialPricing
      );
    setCheckoutDetails(checkoutDetails=>({...checkoutDetails,items:cartState,prices}))
},[cartState])
// console.log(checkoutDetails)
return <CheckoutContext.Provider value={{checkoutDetails,setCheckoutDetails}}>
        {children}
    </CheckoutContext.Provider>
}
export const useCheckout=()=>useContext(CheckoutContext);
