import { useProducts } from "../../context/productsContext";
export const Home = () => {
const {receivedProducts}=useProducts();
console.log(receivedProducts);
const product1=receivedProducts[Math.floor(Math.random()*receivedProducts.length)]
const product2=receivedProducts[Math.floor(Math.random()*receivedProducts.length)]
console.log(product1,product2);
  return (
    <div>
      <Slider />
      <Categories />
      <div
        style={{
          display: "flex",
          width: "100%",
          justifyContent: "center",
          marginTop: "1rem",
        }}
      >
        <div
          style={{
            width: "40%",
            height: "30vh",
            backgroundColor: "grey",
            margin: "0.5rem 1rem",
          }}
        ></div>
        <div
          style={{
            width: "40%",
            height: "30vh",
            backgroundColor: "grey",
            margin: "0.5rem 1rem",
          }}
        ></div>
      </div>
    </div>
  );
};
