import { useState, createContext, useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { userDetails } from "../services/getUser";
export const AuthContext = createContext();
export const AuthProvider = ({ children }) => {
  const [isLogin, setIsLogin] = useState(
    localStorage.getItem("token") ? true : false
  );
  const [currentUser, setCurrentUser] = useState({
    firstName: "",
    lastName: "",
    gender: "",
    email:"",
    address: {
      city: "",
      state: "",
      country: "",
      pin: "",
    },
  });
  const location = useLocation();
  const navigate = useNavigate();
  const user = async (email, password) => {
    try {
      const response = await userDetails({ email, password });
      if (response?.encodedToken === undefined) throw response;
      else {
        const { token, firstName, lastName, email, gendermaddress } = response;
        // localStorage.setItem("token", response?.encodedToken);
        // localStorage.setItem("user", response?.foundUser?.firstName);
        // localStorage.setItem("email", response?.foundUser?.email);
        localStorage.setItem("token", token);
        setCurrentUser({
          firstName,
          lastName,
          gender,
          email,
          address,
        });
        console.log(currentUser);
      }
      if (localStorage.getItem("token")) {
        setIsLogin(true);
        navigate(location?.state?.pathname);
      }
    } catch (e) {
      console.log(e);
    }
  };
  const logOut = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    localStorage.removeItem("email");
    setIsLogin(false);
  };
  // const response=userDetails({email: "adarshbalika@gmail.com", password: "adarshbalika",});
  //     console.log(response)
  return (
    <AuthContext.Provider value={{ isLogin, setIsLogin, user, logOut }}>
      {children}
    </AuthContext.Provider>
  );
};
export const useAuth = () => useContext(AuthContext);
