import {
    useState,
    useEffect,
    createContext,
    useContext,
    useReducer,
  } from "react";
  import { getWishList } from "../services/getWishlistItems";
import { addProductToWishlist } from "../services/addProductToWishlist";
import { useAuth } from "./authContext";
  export const WishlistContext = createContext();
  export const WishlistProvider = ({ children }) => {
    const { authState } = useAuth();
    const token = localStorage.getItem("token");
    const getUserWishlist = async (searchToken) => {
      try {
        const wishlist = await getWishList(searchToken);
        // cartDispatch({ type: "LOAD_CART", payload: cart });
        console.log(wishlist);
      } catch (e) {
        console.log(e);
      }
    };
    const addItemToCart = async (product) => {
      if (token) {
        try {
          const updatedWishlist = await addProductToWishlist(product, token);
        //   cartDispatch({ type: "LOAD_CART", payload: updatedCart });
        console.log(updatedWishlist);
        } catch (e) {
          console.log(e);
        }
      }
      else{
          alert('Log in first');
      }
    };
    useEffect(() => {
      if (token) {
        getUserWishlist(token);
      }
    }, [authState]);
    return (
      <WishlistContext.Provider value={{addItemToCart }}>
        {children}
      </WishlistContext.Provider>
    );
  };
  export const useCart = () => useContext(WishlistContext);
