import {
  useState,
  useEffect,
  useContext,
  createContext,
  useReducer,
} from "react";
import { getProducts } from "../services/getAllProducts";
import {
  initialFilter,
  filterReducerFunction,
} from "../reducers/filterReducer";
export const ProductsContext = createContext();
export const ProductsProvider = ({ children }) => {
  const [filterState, filterDispatch] = useReducer(
    filterReducerFunction,
    initialFilter
  );
  const [products, setProducts] = useState({
    showProducts: [],
    receivedProducts: [],
  });
  useEffect(() => {
    (async () => {
      const response = await getProducts();
      // console.log(response);
      setProducts({
        showProducts: response,
        receivedProducts: response,
      });
    })();
  }, []);
  const { showProducts } = products;
  const { sortBy, categories, rating, maxPrice, search } = filterState;
  console.log(filterState);
  const categoryFilter = showProducts.filter((product) => {
    console.log(parseInt(product.price))
    if (categories.includes(product.category)&&product.rating>rating && parseInt(product.price.replace(',',''))<=parseInt(maxPrice) )
      return true;
      return false;
 
  });
  // const sortedProducts =
  //   sortBy !== ""
  //     ? sortBy === "low"
  //       ? categoryFilter.sort((a, b) =>
  //           parseFloat(a.price) < parseFloat(b.price)
  //             ? -1
  //             : parseFloat(a.price) > parseFloat(b.price)
  //             ? 1
  //             : 0
  //         )
  //       : categoryFilter.sort((a, b) =>
  //           parseFloat(a.price) > parseFloat(b.price)
  //             ? -1
  //             : parseFloat(a.price) < parseFloat(b.price)
  //             ? 1
  //             : 0
  //         )
  //     : categoryFilter;
  // console.log(sortedProducts);
  let sortedProducts = categoryFilter;
  if (sortBy === "") {
    sortedProducts = categoryFilter;
  } else {
    if (sortBy === "low") {
      sortedProducts = sortedProducts.sort(
        (a, b) => parseInt(a.price) - parseInt(b.price)
      );
    } else {
      sortedProducts = sortedProducts.sort(
        (a, b) => parseInt(b.price) - parseInt(a.price)
      );
    }
  }
  // console.log(categoryFilter);
  // console.log(sortedProducts);
  return (
    <ProductsContext.Provider
      value={{ sortedProducts, filterDispatch, filterState }}
    >
      {children}
    </ProductsContext.Provider>
  );
};
export const useProducts = () => useContext(ProductsContext);
