import { useState, useEffect } from "react";
import { useCart } from "../context/cartContext";

export const PriceCard = () => {
  const { cartState } = useCart();
  const [priceDetails, setPrice] = useState({
    items: 0,
    price: 0,
    discount: 0,
    totalPrice: 0,
  });
  const update = () => {
    const newObj = cartState.reduce(
      (acc, { qty, price, offerPrice }) => ({
        items: cartState.length,
        price: (acc.price += parseInt(price.replace(",", ""))*qty),
        discount: acc.discount+=(parseInt(price.replace(",", "")) - parseInt(offerPrice.replace(",", "")))*qty,
      }),
      {
        items: 0,
        price: 0,
        discount: 0,
        totalPrice: 0,
      }
    );
    setPrice(cartState.length===0?{
        items: 0,
        price: 0,
        discount: 0,
        totalPrice: 0,
      }:newObj);
  };
  useEffect(()=>{
    update();   
  },[cartState])
  console.log(cartState);
  console.log(priceDetails);
  return <></>;
};
