import { useState, createContext, useContext, useReducer,useEffect} from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { userDetails } from "../services/getUser";
import {initialUser,authReducerFun} from "../reducers/authReducer";
export const AuthContext = createContext();
export const AuthProvider = ({ children }) => {
  const [authState,authDispatch]=useReducer(authReducerFun,initialUser);
  const [isLogin, setIsLogin] = useState(
    localStorage.getItem("token") ? true : false
  );
  useEffect(()=>{
    authDispatch({type:'LOAD_DATA'});
  },[]);
  console.log(authState);
  const location = useLocation();
  const navigate = useNavigate();
  const user = async (email, password) => {
    try {
      const response = await userDetails({ email, password });
      if (response?.encodedToken === undefined) throw response;
      else {
        const {
          encodedToken,
          foundUser: { firstName, lastName, email, gender,phone, address:{city,state,country,pin} },
        } = response;
        localStorage.setItem("token", encodedToken);
        localStorage.setItem("firstName",firstName);
        localStorage.setItem("lastName",lastName);
        localStorage.setItem("email",email);
        localStorage.setItem("gender",gender);
        localStorage.setItem("city",city);
        localStorage.setItem("state",state);
        localStorage.setItem("country",country);
        localStorage.setItem("pin",pin);
        localStorage.setItem("phone",phone);
        authDispatch({type:'LOG_USER',payload:{firstName,lastName,email,gender,phone,address:{city,state,country,pin},loginStatus:true}})
      }
      if (localStorage.getItem("token")) {
        setIsLogin(true);
        navigate(location?.state?.pathname);
      }
    } catch (e) {
      console.log(e);
    }
  };
  const logOut = () => {
    localStorage.clear()
    setIsLogin(false);
  };
  return (
    <AuthContext.Provider
      value={{ isLogin, setIsLogin, logOut,authState,authDispatch,user}}
    >
      {children}
    </AuthContext.Provider>
  );
};
export const useAuth = () => useContext(AuthContext);
