const initialFilter = {
  sortBy: "",
  categories: {
    computerAccessories: false,
    mobiles: false,
    computers: false,
    networking: false,
    software: false,
  },
  rating: "1",
  maxPrice: "50000",
  search: "",
};
const filterReducerFunction = (state, action) => {
  const { type, payload } = action;
  switch (type) {
    case "SORT_BY_PRICE":
      return { ...state, sortBy: payload };
    case "SORT_BY_CATEGORY":
      return {
        ...state,
        categories: {
          ...state.categories,
          [payload]: !state[payload],
        },
      };
    case "SORT_BY_RATING":return {...state,rating:payload}
    case "RESET_FILTERS":return {...initialFilter};

    default:
      return state;
  }
};
export { initialFilter, filterReducerFunction };
