import { useState } from "react";
import { useAuth } from "../context/authContext";
import { useCart } from "../context/cartContext";
import { useWishlist } from "../context/wishistContext";
import "../pages/User/viewUser.css";
import MaleUser from "../../images/maleUser.png";
import FemaleUser from "../../images/femaleUser.png";
import { useUser } from "../context/userContext";
export const ShowUser = () => {
  const { authDispatch,setIsLogin,authState } = useAuth();
  const { cartDispatch } = useCart();
  const { wishlistDispatch } = useWishlist();
  const {addresses}=useUser();
  const {
    firstName,
    lastName,
    gender,
    email
  } = authState;
  const [showTab,setShowTab]=useState(0);
  console.log(addresses)
  return (
    <div className="userMenu">
      {/* <h2
        style={{ margin: "0", padding: "1rem" }}
      >{`${firstName} ${lastName}`}</h2>
      <p>Email:{email}</p>
      <p>Gender:{gender}</p>
      <p>Phone Number: {phone}</p>
      <p>Address: {`${city},${state},${country}`}</p>
      <p>Pin Code: {pin}</p>
      <button
        onClick={() => {
          authDispatch({ type: "LOG_OUT" });
          cartDispatch({ type: "RESET" });
          wishlistDispatch({ type: "RESET" });
          setIsLogin(false);
        }}
      >
        LogOut
      </button> */}
    <div className="userTabs">
      <div className="tabs" onClick={()=>setShowTab(0)} style={{borderBottom:showTab===0 && "2px solid red"}}>User Info</div>
      <div className="tabs" onClick={()=>setShowTab(1)} style={{borderBottom:showTab===1 && "2px solid red"}}>Orders</div>
      <div className="tabs" onClick={()=>setShowTab(2)} style={{borderBottom:showTab===2 && "2px solid red"}}>Addresses</div>
    </div>
    <div style={{display:showTab===0?"block":"none"}}>
      <img src={gender.toLowerCase()==="female"?FemaleUser:MaleUser} alt="userAvatar" className="avatar" />
      <h2>{`${firstName} ${lastName}`}</h2>
      <p>Email: {email}</p>
      <p>Gender: {gender}</p>
      <button
        onClick={() => {
          authDispatch({ type: "LOG_OUT" });
          cartDispatch({ type: "RESET" });
          wishlistDispatch({ type: "RESET" });
          setIsLogin(false);
        }}
      >
        LogOut
      </button> 
    </div>
    <div style={{display:showTab===1?"block":"none"}}>OrdersHere</div>
    <div style={{display:showTab===2?"block":"none"}}>{
      addresses.map((address,index)=>{
        const {city,state,country,pinCode,phone,firstName,lastName}=address
        return <div key={index} className="addressCards">
        <p>{firstName} {lastName}</p>
        <p>Phone: {phone}</p>
        <p>{city},{state},{country},{pinCode}</p>
        </div>
      })
    }</div>

    </div>
  );
};
