import axios from "axios";
export const getCart = async () => {
  const encodedToken = localStorage.getItem("token");
  try {
    const {status,data} = await axios.get("/api/user/cart", {
      headers: { authorization: encodedToken },
    });
    if(status===200){
      return data;
    }
  } catch (e) {
    console.log(e);
  }
};
