import { useState,useEffect } from "react";
import { useCart } from "../context/cartContext"

export const PriceCard=()=>{
    const {cartState}=useCart();
    const [priceDetails,setPrice]=useState({
        items:0,
        price:0,
        discount:0,
        totalPrice:0
    });
    useEffect(()=>{
        const newObj=cartState.reduce((acc,{qty,price,offerPrice})=>({
            items:acc.items+=qty,
            price:acc.price+=parseInt(price.replace(",",""))*qty,
            discount:acc.price-parseInt(offerPrice.replace(",","")),
            totalPrice:acc.price-acc.discount
        }),priceDetails)
        setPrice(newObj);
    },[cartState]);
    console.log(priceDetails);
    return<></>
}
