export const searchedProducts=(allProducts,searchString)=>{
    searchString=searchString
    if(searchString!=="")
    return allProducts.filter(product=>{
        const {title,description,category}=product;
        return title.includes(searchString) ||  category.includes(searchString);
    })
    return allProducts;
}
