import { useState, useEffect, useContext, createContext,useReducer } from "react";
import { getProducts } from "../services/getAllProducts";
import { initialFilter,filterReducerFunction } from "../reducers/filterReducer";
export const ProductsContext = createContext();
export const ProductsProvider = ({ children }) => {
  const [filterState,filterDispatch]=useReducer(filterReducerFunction,initialFilter);
  const [products, setProducts] = useState({
    showProducts:[],
    receivedProducts:[]
  });
  useEffect(() => {
    (async () => {
      const response = await getProducts();
      console.log(response);
      setProducts({
        showProducts:response,
        receivedProducts:response
      });
    })();
  }, []);
  const {showProducts}=products
  console.log(filterState);
  const categoryFilter=showProducts.filter(({category})=>
    {
      if(filterState.categories.includes(category))
      return true;
      return false;
    }
    )
  console.log(categoryFilter);
  return (
    <ProductsContext.Provider value={{ products,filterDispatch}}>
      {children}
    </ProductsContext.Provider>
  );
};
export const useProducts = () => useContext(ProductsContext);
