import { useState } from "react";
import { useAuth } from "../context/authContext";
import { useCart } from "../context/cartContext";
import { useWishlist } from "../context/wishistContext";
import "../pages/User/viewUser.css";
import MaleUser from "../../images/maleUser.png";
import FemaleUser from "../../images/femaleUser.png";
import { useUser } from "../context/userContext";
import { AddressForm } from "./addressForm";
export const ShowUser = () => {
  const { authDispatch, setIsLogin, authState } = useAuth();
  const { cartDispatch } = useCart();
  const { wishlistDispatch } = useWishlist();
  const { addresses, addNewAddress, updateAddress,removeAddress } = useUser();
  const { firstName, lastName, gender, email } = authState;
  const [showTab, setShowTab] = useState(0);
  const [update, setUpdate] = useState(-1);
  const [showForm, setShowForm] = useState(false);
  const initialData = {
    firstName: "",
    lastName: "",
    gender: "",
    city: "",
    state: "",
    country: "",
    pinCode: "",
    phone: "",
  };
  const [newAddress, setNewAddress] = useState(initialData);
  const changeHandler = (attribute, value) => {
    setNewAddress({ ...newAddress, [attribute]: value });
  };
  const submitForm = (e) => {
    e.preventDefault();
    if (update !== -1) {
      updateAddress(update, newAddress);
      setUpdate(-1);
    } else addNewAddress(newAddress);
    setShowForm(false);
  };
  const updateHandler = (index) => {
    setNewAddress(addresses[index]);
    setShowForm(true);
    setUpdate(index);
  };
  const removeHandler=(index)=>{
    if(index!==-1)
    {
      removeAddress(index);
    }
    // setRemove(-1);
  }
  console.log(addresses);
  // const {firstName:formFirstName,lastName:formLastName,gender:formGender,phone,city,state,country,pinCode}=newAddress
  return (
    <>
      <div className="userMenu">
        {/* <h2
        style={{ margin: "0", padding: "1rem" }}
      >{`${firstName} ${lastName}`}</h2>
      <p>Email:{email}</p>
      <p>Gender:{gender}</p>
      <p>Phone Number: {phone}</p>
      <p>Address: {`${city},${state},${country}`}</p>
      <p>Pin Code: {pin}</p>
      <button
        onClick={() => {
          authDispatch({ type: "LOG_OUT" });
          cartDispatch({ type: "RESET" });
          wishlistDispatch({ type: "RESET" });
          setIsLogin(false);
        }}
      >
        LogOut
      </button> */}
        <div className="userTabs">
          <div
            className="tabs"
            onClick={() => setShowTab(0)}
            style={{ borderBottom: showTab === 0 && "2px solid red" }}
          >
            User Info
          </div>
          <div
            className="tabs"
            onClick={() => setShowTab(1)}
            style={{ borderBottom: showTab === 1 && "2px solid red" }}
          >
            Orders
          </div>
          <div
            className="tabs"
            onClick={() => setShowTab(2)}
            style={{ borderBottom: showTab === 2 && "2px solid red" }}
          >
            Addresses
          </div>
        </div>
        <div
          style={{ display: showTab === 0 ? "block" : "none" }}
          className="tabMenu"
        >
          <img
            src={gender.toLowerCase() === "female" ? FemaleUser : MaleUser}
            alt="userAvatar"
            className="avatar"
          />
          <h2>{`${firstName} ${lastName}`}</h2>
          <p>Email: {email}</p>
          <p>Gender: {gender}</p>
          <button
            onClick={() => {
              authDispatch({ type: "LOG_OUT" });
              cartDispatch({ type: "RESET" });
              wishlistDispatch({ type: "RESET" });
              setIsLogin(false);
            }}
          >
            LogOut
          </button>
        </div>
        <div
          style={{ display: showTab === 1 ? "block" : "none" }}
          className="tabMenu"
        >
          OrdersHere
        </div>
        <div
          style={{ display: showTab === 2 ? "block" : "none" }}
          className="tabMenu"
        >
          {addresses.map((address, index) => {
            const {
              city,
              state,
              country,
              pinCode,
              phone,
              firstName,
              lastName,
            } = address;
            return (
              <div key={index} className="addressCards">
                <p>
                  {firstName} {lastName}
                </p>
                <p>Phone: {phone}</p>
                <p>
                  {city},{state},{country},{pinCode}
                </p>
                <button onClick={() => updateHandler(index)}>Update</button>
                <button onClick={() => removeHandler(index)}>Remove</button>
              </div>
            );
          })}
          <button
            onClick={() => {
              setShowForm(true);
              setNewAddress(initialData);
            }}
          >
            Add new Address
          </button>
        </div>
      </div>
    <AddressForm/>
    </>
  );
};
