import {
  useState,
  useEffect,
  useContext,
  createContext,
  useReducer,
} from "react";
import { getProducts } from "../services/getAllProducts";
import {
  initialFilter,
  filterReducerFunction,
} from "../reducers/filterReducer";
export const ProductsContext = createContext();
export const ProductsProvider = ({ children }) => {
  const [filterState, filterDispatch] = useReducer(
    filterReducerFunction,
    initialFilter
  );
  const [products, setProducts] = useState({
    showProducts: [],
    receivedProducts: [],
  });
  useEffect(() => {
    (async () => {
      const response = await getProducts();
      console.log(response);
      setProducts({
        showProducts: response,
        receivedProducts: response,
      });
    })();
  }, []);
  const { showProducts } = products;
  const { sortBy, categories, rating, maxPrice, search } = filterState;
  console.log(filterState);
  const categoryFilter = showProducts.filter(({ category }) => {
    if (!categories.includes(category) && categories.length === 0) return false;
    return true;
  });
  // const sortedProducts=(sortBy!=="")?(sortBy==="low")?categoryFilter.sort((a,b)=>Number(a.price)-Number(b.price)):categoryFilter.sort((a,b)=>Number(b.price)-Number(a.price)):categoryFilter;
  // console.log(sortedProducts);
  let sortedProducts = categoryFilter;
  if (sortBy!=="") {
    sortedProducts = categoryFilter;
  } else {
    console.log("SortOrder", sortBy);
    if (sortBy==="low") {
      categoryFilter.sort((a, b) => Number(a.price) - Number(b.price));
      console.log(sortBy);
    } else {
      categoryFilter.sort((a, b) => Number(b.price) - Number(a.price));
      console.log(sortBy);
    }
  }
  console.log(categoryFilter);
  console.log(sortedProducts);
  return (
    <ProductsContext.Provider value={{ sortedProducts, filterDispatch }}>
      {children}
    </ProductsContext.Provider>
  );
};
export const useProducts = () => useContext(ProductsContext);
