        <img src={image} alt={title} className="productDetailImage"/>
        <div>
            <h2>{title}</h2>
            <p>{category}</p>
            <p><span>₹{offerPrice}</span> <span>₹{price}</span> <span>{discount}</span></p>
        </div>
    </div>
}
